import React, {Component} from 'react'
import {Layout, Menu } from 'antd';

import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        this.state={
            theme:'light'
        }
        this.props={
        }

        this.favouritesMenuList = [
            {
                title:'常用',
            },
            {
                title:'文献',
                children:[{
                    title:'中文文献',
                },{
                    title:'英文文献',
                }],
            },
        ]

        this.menuNodes = this.getMenuNodes(this.favouritesMenuList)
    }

    getMenuNodes = (menuList)=>{
        return menuList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.key}>
                        {item.title}
                    </Menu.Item>
                )
            }else{
                return(
                    <SubMenu key={item.title} title={item.title}>
                        {this.getMenuNodes(item.children)}
                    </SubMenu>
                )
            }
        })
    }

    render(){
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={['1']} defaultOpenKeys={['sub1']}>
                    {this.menuNodes}
                </Menu>
            </Sider>
        )
    }
}
