/*
包含应用中所有api
 */
import ajax from './ajax'
import md5 from 'md5'


//登入
export const reqLogin = (username, password) => {
    password = md5(password)
    return ajax('/api/login', {username, password}, 'POST')
}
//获取用户列表
export const reqUserList = () => ajax('/api/user', {'type':'get'}, 'POST')
//更新用户
export const reqUpdateUser = (username, password, email) => {
    password = md5(password)
    ajax('/api/user', {'type':'add', username, password, email}, 'POST')
}
//删除用户
export const reqDelUser = (username) => ajax('/api/user', {'type':'del', username}, 'POST')
//获取角色列表
export const reqAuthorityList = () => ajax('/api/authority', {'type':'get'}, 'POST')
//更新角色
export const reqUpdateAuthority = (name, menus='') => ajax('/api/authority', {'type':'update', name, menus}, 'POST')
//删除角色
export const reqDelAuthority = (name) => ajax('/api/authority', {'type':'del', name}, 'POST')
