import React, {Component} from 'react'
import {Layout, Menu, Button } from 'antd';
import {
    PlusCircleOutlined,
  } from '@ant-design/icons';

import FavInput from '../favInput/favInput'
import FavMenu from '../favMenu/favMenu'
import './leftNav.less'

/*
左侧导航的组件
 */
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        const favouritesMenuList = ['常用','文献']
        this.state={
            theme:'light',
            menuList:favouritesMenuList,
            onAddStatus: false,
        }
    }

    getMenuNodes = (menuList)=>{
        return menuList.map(item => {
            return(<Menu.Item key={item}><FavMenu title={item} editFavType={this.editFavType}/></Menu.Item>)
        })
    }

    onAdd = ()=>{
        this.setState({onAddStatus:true})
    }

    editFavType = (oldName, newName)=>{
        console.log(oldName, newName)
    }

    addFavType = (typeName)=>{
        let {menuList} = this.state
        if(typeName){
            menuList.push(typeName)
        }
        this.setState({onAddStatus:false, menuList:menuList})
    }

    render(){
        const {menuList, onAddStatus} = this.state
        const menuNodes = this.getMenuNodes(menuList)
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={[menuList[0].title]}>
                    {menuNodes}
                    <Menu.Item key='new-menu' style={{display:onAddStatus ? 'block' : 'none'}}>
                        <FavInput addFavType={this.addFavType}/>
                    </Menu.Item>
                </Menu>
                <Button type='link' style={{width:"100%"}} onClick={this.onAdd}>
                    <PlusCircleOutlined style={{ fontSize: '20px'}}/>
                </Button>
            </Sider>
        )
    }
}
