import React, {Component} from 'react'
import { Row,Col,Button,Tooltip,Modal,Form,Input,Rate,message } from 'antd';
import { PlusOutlined } from '@ant-design/icons'

import FavouritesCard from '../favCard/favCard'
import {reqUpdateItem} from '../../../../../api'

/*
左侧导航的组件
 */
export default class CardContent extends Component{
    state = {
        showStatus:0,
    }
    addFormRef = React.createRef()
    editFormRef = React.createRef()

    getCardNodes = (itemList)=>{
        return itemList.map(item => {
            return(
                <Col key={item.url}>
                    <FavouritesCard url={item.url} title={item.title}
                    description={item.description} rank={item.rank}
                    update={this.props.update} onEdit={this.onEdit}/>
                </Col>
            )
        })
    }

    updateItem = (ifAdd)=>{
        if(ifAdd){
            this.addFormRef.current.validateFields().then(values => {
                const {url, title, description, rank} = values
                reqUpdateItem(url, this.props.favType, title, rank, description).then(data=>{
                    if(data.status==='0'){
                        this.props.update()
                        this.afterAdd()
                        message.success('添加成功！')
                    }
                })
            }).catch(info => message.error('请正确填写信息！'));
        }else{
            this.editFormRef.current.validateFields().then(values => {
                const {url, title, description, rank} = values
                reqUpdateItem(url, this.props.favType, title, rank, description).then(data=>{
                    if(data.status==='0'){
                        this.props.update()
                        this.afterAdd()
                        message.success('添加成功！')
                    }
                })
            }).catch(info => message.error('请正确填写信息！'));
        }

    }

    onEdit = (url, title, description, rank)=>{
        ()=>this.setState({showStatus:2})
    }

    afterAdd = ()=>{
        this.setState({showStatus:0})
        this.formRef.current.resetFields();
    }

    render(){
        const {itemList} = this.props
        const cardNodes = this.getCardNodes(itemList)
        return(
            <div>
                <Row gutter={[16, 24]}>
                    {cardNodes}
                    <Tooltip title="添加一个">
                        <Button icon={<PlusOutlined style={{fontSize:'50px', color:'#C0C0C0'}}/>} 
                        style={{height:'200px', width:'200px'}}
                        onClick={()=>{this.setState({showStatus:1})}}/>
                    </Tooltip>
                </Row>
                <Modal title='添加收藏项' visible={this.state.showStatus===1} 
                onOk={()=>{this.updateItem(true)}} onCancel={this.afterAdd}>
                    <Form ref={this.addFormRef}>
                        <Form.Item name="url"
                        rules={[{required: true,message: '请输入URL'},]}>
                            <Input placeholder="请输入URL" />
                        </Form.Item>
                        <Form.Item name="title"
                        rules={[{required: true,message: '请输入标题'},]}>
                            <Input placeholder="请输入标题" />
                        </Form.Item>
                        <Form.Item name="description"
                        rules={[{required: true,message: '请输入相关描述'},]}>
                            <Input placeholder="请输入相关描述" />
                        </Form.Item>
                        <Form.Item name="rank" initialValue={3}>
                            <Rate/>
                        </Form.Item>
                    </Form>
                </Modal>
                <Modal title='编辑收藏项' visible={this.state.showStatus===2} 
                onOk={()=>{this.updateItem(false)}} onCancel={this.afterAdd}>
                    <Form ref={this.editFormRef}>
                        <Form.Item name="url"
                        rules={[{required: true,message: '请输入URL'},]}>
                            <Input placeholder="请输入URL" />
                        </Form.Item>
                        <Form.Item name="title"
                        rules={[{required: true,message: '请输入标题'},]}>
                            <Input placeholder="请输入标题" />
                        </Form.Item>
                        <Form.Item name="description"
                        rules={[{required: true,message: '请输入相关描述'},]}>
                            <Input placeholder="请输入相关描述" />
                        </Form.Item>
                        <Form.Item name="rank" initialValue={3}>
                            <Rate/>
                        </Form.Item>
                    </Form>
                </Modal>
            </div>
        )
    }
}
