import React, {Component} from 'react'
import {Layout, Menu } from 'antd';

import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        this.state={
            theme:'light'
        }
        this.props={
            favouritesMenuList:[
                {
                    key:'常用',
                    title:'常用',
                },
                {
                    key:'文献',
                    title:'文献',
                    children:[{
                        key:'中文文献',
                        title:'中文文献',
                    },{
                        key:'英文文献',
                        title:'英文文献',
                    }],
                },
            ]
        }
    }

    getMenuNodes = ()=>{
        const menuList = this.props
        return menuList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.key} icon={<item.icon/>}>
                        <Link to={'/admin/'+item.key}>{item.title}</Link>
                    </Menu.Item>
                )
            }else{
                let cItem = item.children.find(cItem => cItem.key === this.props.path.split('/')[2])
                if(cItem)this.openKey = item.key
                return(
                    <SubMenu key={item.key} icon={<item.icon/>} title={item.title}>
                        {this.getMenuNodes(item.children, menus)}
                    </SubMenu>
                )
            }
        })
    }

    render(){
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={['1']} defaultOpenKeys={['sub1']}
                >
                    <Menu.Item key="0">option1</Menu.Item>
                    <SubMenu key="sub1" title="subnav 1">
                    <Menu.Item key="1">option1</Menu.Item>
                    <Menu.Item key="2">option2</Menu.Item>
                    <Menu.Item key="3">option3</Menu.Item>
                    <Menu.Item key="4">option4</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" title="subnav 2">
                    <Menu.Item key="5">option1</Menu.Item>
                    <Menu.Item key="6">option2</Menu.Item>
                    <Menu.Item key="7">option3</Menu.Item>
                    <Menu.Item key="8">option4</Menu.Item>
                    </SubMenu>
                </Menu>
            </Sider>
        )
    }
}
