import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import { Menu } from 'antd';
// import {
//     HomeOutlined,
//     PieChartOutlined,
//     FormOutlined,
//     UserOutlined,
//     SettingOutlined,
//   } from '@ant-design/icons';

import menuList from '../../config/menuConfig'
import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;

export default class LeftNav extends Component{
    /**
     * 根据menu的数据数组生成对应标签数组
     * @param {*} menuList 
     */
    getMenuNodes = (menuList) =>{
        return menuList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.key} icon={item.icon}>
                        <Link to={'/admin/'+item.key}>统计</Link>
                    </Menu.Item>
                )
            }else{
                return(
                    <SubMenu key="sub1" icon={<FormOutlined />} title="管理">
                        <Menu.Item icon={<UserOutlined />} key="3">
                            <Link to='/admin/statistics'>用户管理</Link>
                        </Menu.Item>
                    </SubMenu>
                )
            }
        })
    }
    render(){
        return(
            <div className='left-nav'>
                <Link to='/' className='left-nav-header'>
                    <h1>后台管理</h1>
                </Link>
                <Menu defaultSelectedKeys={['1']} mode="inline" theme="dark">
                    this.getMenuNodes(menuList)
                    {/* <Menu.Item key="1" icon={<HomeOutlined />} to='/admin/home'>
                        <Link to='/admin/home'>
                            首页
                        </Link>
                    </Menu.Item>
                    <Menu.Item key="2" icon={<PieChartOutlined />}>
                        <Link to='/admin/statistics'>
                            统计
                        </Link>
                    </Menu.Item>
                    <SubMenu key="sub1" icon={<FormOutlined />} title="管理">
                        <Menu.Item icon={<UserOutlined />} key="3">用户管理</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" icon={<    <Menu.Item icon={<SettingOutlined />} key="4">基础设置</Menu.Item>
                    </SubMenu> */}SettingOutlined />} title="设置">

                </Menu>
            </div>
        )
    }
}
