import React, {Component} from 'react'
import {Link} from 'react-router-dom'
import { Menu } from 'antd';

import menuList from '../../config/menuConfig'
import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;

export default class LeftNav extends Component{
    /**
     * 根据menu的数据数组生成对应标签数组
     */
    getMenuNodes = (menuList) =>{
        return menuList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.key} icon={<item.icon/>}>
                        <Link to={'/admin/'+item.key}>{item.title}</Link>
                    </Menu.Item>
                )
            }else{
                let cItem = item.children.find(cItem => cItem.key === this.props.path.split('/')[2])
                if(cItem)this.openKey = item.key
                return(
                    <SubMenu key={item.key} icon={<item.icon/>} title={item.title}>
                        {this.getMenuNodes(item.children)}
                    </SubMenu>
                )
            }
        })
    }
    render(){
        const menuNodes = this.getMenuNodes(menuList)
        return(
            <div className='left-nav'>
                <Link to='/' className='left-nav-header'>
                    <h1>后台管理</h1>
                </Link>
                <Menu selectedKeys={[this.props.path.split('/')[2]]}  mode="inline" theme="dark">
                    {this.getMenuNodes(menuList)}
                </Menu>
            </div>
        )
    }
}
