import React, {Component} from 'react'
import {Layout, Menu, Button } from 'antd'
import {
    PlusCircleOutlined,
    DeleteOutlined,
  } from '@ant-design/icons';

import {reqFavTypeList} from '../../../../../api'
import FavInput from '../favInput/favInput'
import FavMenu from '../favMenu/favMenu'
import './leftNav.less'
/*
左侧导航的组件
 */
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        // const favouritesMenuList = [{rank:0, title:''}]
        this.state={
            theme:'light',
            menuList:[],
            onAddStatus: false,
            selectedMenu:'',
            dragingMenu:{},
            targetMenu:{},
        }
    }

    componentDidMount(){
        this.updateMenuList()

    }

    updateMenuList = ()=>{
        reqFavTypeList().then(data=>{
            this.setState({menuList:data.data})
            if(!this.state.selectedMenu){
                this.setState({selectedMenu:data.data[0].title})
            }
        })
    }

    onDragStart = (ev)=>{
        const dragingMenu = ev.target.childNodes[0].childNodes[0].innerHTML
        this.setState({dragingMenu:dragingMenu})
    }

    onDropOver = (ev)=>{
        ev.preventDefault()
        const {dragingMenu} = this.state
        if(dragingMenu){
            const targetMenu = ev.target.innerHTML
            if(dragingMenu !== targetMenu){
                this.setState({targetMenu:targetMenu})
            }
        }
    }

    onDrop = (ev)=>{
        //TODO 再次定义排序
        //初始化
        this.setState({targetMenu:{}, dragingMenu:{}})
    }

    getMenuNodes = (menuList)=>{
        const {targetMenu} = this.state
        return menuList.map(item => {
            let targetStyle = {}
            if(targetMenu===item.title){
                targetStyle = {borderBottom:"2px dashed #1890ff"}
            }
            return(
                <Menu.Item key={item.title} style={targetStyle} draggable={true} 
                onDragStart={this.onDragStart} onDragOver={this.onDropOver}
                onDrop={this.onDrop}
                >
                    <FavMenu title={item.title} editFavType={this.editFavType}/>
                </Menu.Item>
            )
        })
    }

    onAdd = ()=>{
        this.setState({onAddStatus:true})
    }

    onDel = ()=>{
        console.log('Del' + this.state.selectedMenu)
        //TODO 删除选中项
    }

    onSelect = (value)=>{
        this.setState({selectedMenu:value.key})
    }

    editFavType = (oldName, newName)=>{
        console.log(oldName, newName)
        //TODO 编辑收藏类名
    }

    addFavType = (typeName)=>{
        //TODO 添加收藏类名
        let {menuList} = this.state
        if(typeName){
            const rank = menuList.length
            menuList.push({rank:rank, title:typeName})
        }
        this.setState({onAddStatus:false, menuList:menuList})
    }

    render(){
        const {menuList, onAddStatus, selectedMenu} = this.state
        const menuNodes = this.getMenuNodes(menuList)
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                selectedKeys={[selectedMenu]}
                onSelect={this.onSelect}>
                    {menuNodes}
                    <Menu.Item  key='new-menu' style={{display:onAddStatus ? 'block' : 'none'}}>
                        <FavInput editFavType={this.addFavType}/>
                    </Menu.Item>
                </Menu>
                <div>
                    <Button type='link' style={{width:"50%"}} onClick={this.onAdd}>
                        <PlusCircleOutlined style={{ fontSize: '20px'}}/>
                    </Button>
                    <Button type='link' style={{width:"50%"}} onClick={this.onDel}>
                        <DeleteOutlined style={{ fontSize: '20px'}}/>
                    </Button>
                </div>
            </Sider>
        )
    }
}
