import React, {Component} from 'react'
import {Modal, Tree} from 'antd'

import menuList from '../../../../../config/menuConfig'
/*
管理-统计
 */
const {TreeNode} = Tree

export default class AddForm extends Component{
    setRole = ()=>{

    }

    getMenuNodes = (menuList) =>{
        return menuList.map(item => {
            if(!item.children){
                return(
                    <Menu.Item key={item.key} icon={<item.icon/>}>
                        <Link to={'/admin/'+item.key}>{item.title}</Link>
                    </Menu.Item>
                )
            }else{
                let cItem = item.children.find(cItem => cItem.key === this.props.path.split('/')[2])
                if(cItem)this.openKey = item.key
                return(
                    <SubMenu key={item.key} icon={<item.icon/>} title={item.title}>
                        {this.getMenuNodes(item.children)}
                    </SubMenu>
                )
            }
        })
    }

    render(){
        return(
            <Modal title='设置角色权限' visible={this.props.showStatus===2} 
                onOk={this.setRole} onCancel={()=>{this.props.setRole()}}
            >
                <Tree checkable defaultExpandAll={true}>
                    <TreeNode title='平台权限' key='all'>
                        <TreeNode title='parent1-1' key='0-0-0'>
                        </TreeNode>
                    </TreeNode>
                </Tree>
            </Modal>
        )
    }
}
