import React, {Component} from 'react'
import {Layout, Menu, Button, Input } from 'antd';
import {
    PlusCircleOutlined,
  } from '@ant-design/icons';

import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        const favouritesMenuList = ['常用','文献']
        this.state={
            theme:'light',
            menuList:favouritesMenuList,
            otherNodes: '',
        }
    }

    getMenuNodes = (menuList)=>{
        return menuList.map(item => {
            return(<Menu.Item key={item}>{item}</Menu.Item>)
        })
    }

    onAdd = ()=>{
        this.setState({otherNodes:<Menu.Item key='new-menu'><Input></Input></Menu.Item>})
    }

    render(){
        const {menuList, otherNodes} = this.state
        const menuNodes = this.getMenuNodes(menuList)
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={[menuList[0].title]}>
                    {menuNodes}
                    {otherNodes}
                </Menu>
                <Button type='link' style={{width:"100%"}} onClick={this.onAdd}>
                    <PlusCircleOutlined style={{ fontSize: '20px'}}/>
                </Button>
            </Sider>
        )
    }
}
