import React, {Component} from 'react'
import {Card, Avatar, Rate} from 'antd';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';

import { reqUpdateItem } from '../../../../../api'

/*
左侧导航的组件
 */
const { Meta } = Card 

export default class FavouritesCard extends Component{

    onEdit = ()=>{
        console.log('Edit')
    }

    onDelete = ()=>{
        console.log('Delete')
    }

    onRankChange = (value)=>{
        const {address, title, description} = this.props
        reqUpdateItem(address, title, description,value)
    }

    render(){
        const {address, title, description, rank} = this.props
        return(
            <Card style={{ width: 300}}
            bodyStyle={{paddingBottom:10}}
            actions={[
                <EditOutlined key="edit" onClick={this.onEdit}/>,
                <DeleteOutlined key="delete" onClick={this.onDelete}/>,
            ]}
            hoverable={true}
            >
                <Meta
                avatar={<Avatar size="large" shape="square" src={address+"favicon.ico"} />}
                title={title}
                description={description}
                onClick={()=>{window.open(address)}}/>
                <Rate defaultValue={rank} onChange={this.onRankChange}
                 style={{width:'100%', textAlign:"center"}}/>
            </Card>
        )
    }
}
