import React, {Component} from 'react'
import {Card, Button, Table} from 'antd'

import AddForm from './components/addForm'
import {reqAuthorityList} from '../../../../api'

/*
管理-统计
 */
export default class UserManage extends Component{
    state = {
        role: {}, //选中的role
        showStatus:0, //显示状态，0不显示modal,1显示添加modal，2显示设置modal
    }

    componentDidMount(){
        this.updateRole()
    }

    updateRole = () => {
        console.log(123)
        reqAuthorityList().then(data =>{
            this.setState({roles:data.data})
        })
    }

    columns = [{title:'角色名称',dataIndex:'name'},]

    onRow = (role) => {
        return{
            onClick: event =>{
                this.setState({role})
            }
        }
    }

    render(){
        const {roles, role, showStatus} = this.state
        const title=(
            <span>
                <Button type='primary' onClick={()=>{this.setState({showStatus:1})}}>
                    创建角色
                </Button> &nbsp;
                <Button type='primary' onClick={()=>{this.setState({showStatus:2})}} disabled={!role.name}>
                    设置角色权限
                </Button>
            </span>
        )
        return(
            <Card title={title}>
                <Table bordered
                    rowKey='name'
                    dataSource={roles}
                    columns={this.columns}
                    rowSelection={{type: 'radio', selectedRowKeys: [role.name]}}
                    onRow={this.onRow}
                />
                <AddForm showStatus={showStatus} hide={()=>{this.setState({showStatus:0})}} update={this.updateRole}/>
            </Card>
        )
    }
}
