import React, {Component} from 'react'
import {Card,Button,Table} from 'antd'

/*
管理-统计
 */
export default class UserManage extends Component{
    state = {
        roles: [{
            "name": "admin",
            "menus":"home/statistics/user/authority/base"
        }], //所有角色列表
        role: {}, //选中的role
    }
    columns = [
        {
            title:'角色名称',
            dataIndex:'name'
        },
    ]
    onRow = (role) => {
        return{
            onClick: event =>{
                this.setState({role})
            }
        }
    }
    render(){
        const {roles, role} = this.state
        const title=(
            <span>
                <Button type='primary'>创建角色</Button> &nbsp;
                <Button type='primary' disabled={true}>设置角色权限</Button>
            </span>
        )
        return(
            <Card title={title}>
                <Table bordered
                    rowKey='name'
                    dataSource={roles}
                    columns={this.columns}
                    rowSelection={{type: 'radio', selectedRowKeys: [role.name]}}
                    onRow={this.onRow}
                />
            </Card>
        )
    }
}
