/*
包含应用中所有api
 */
import ajax from './ajax'

//登入
export const reqlogin = (username, password) => ajax('/api/login', {username, password}, 'POST')
//添加用户
export const reqAddUser = (user) => ajax('/api/manage/user/add', user, 'POST')
