import React, {Component} from 'react'
import {Layout, Menu, Button } from 'antd';
import {
    PlusCircleOutlined,
  } from '@ant-design/icons';

import './leftNav.less'

/*
左侧导航的组件
 */
const { SubMenu } = Menu;
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        this.favouritesMenuList = [
            {
                title:'常用',
            },
            {
                title:'文献',
                children:[{
                    title:'中文文献',
                },{
                    title:'英文文献',
                }],
            },
        ]
        this.state={
            theme:'light',
            menuNodes:this.getMenuNodes(this.favouritesMenuList)
        }
    }

    getMenuNodes = (menuList)=>{
        return menuList.map(item => {
            if(!item.children){
                return(<Menu.Item key={item.title}>{item.title}</Menu.Item>)
            }else{
                return(
                    <SubMenu key={item.title} title={item.title}>
                        {this.getMenuNodes(item.children)}
                    </SubMenu>
                )
            }
        })
    }

    render(){
        const {menuNodes} = this.state
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={['1']} defaultOpenKeys={['sub1']}>
                    {menuNodes}
                </Menu>
                <Button type='link' style={{width:"100%", height:"50px"}}>
                    <PlusCircleOutlined/>
                </Button>
            </Sider>
        )
    }
}
