import {Card, Button, Table, message} from 'antd'

import {reqUserList} from '../../../../api/index'


/*
管理-统计
 */
export default class UserManage extends Component{
    state = {
        user: {}, //选中的role
        showStatus:0, //显示状态，0不显示modal,1显示添加modal，2显示设置modal
    }

    componentDidMount(){
        this.updateUsers()
    }

    updateUsers = () => {
        reqUserList().then(data =>{
            this.setState({users:data.data})
            console.log(data.data)
        })
    }

    render(){
        const {users, user, showStatus} = this.state
        const title=(
            <span>
                <Button type='primary' onClick={()=>{this.setState({showStatus:1})}}>
                    创建用户
                </Button> &nbsp;
                <Button type='primary' onClick={this.delRole} disabled={!user.name}>
                    删除角色
                </Button> &nbsp;
                <Button type='primary' onClick={()=>{this.setState({showStatus:2})}} disabled={!user.name}>
                    设置角色权限
                </Button>
            </span>
        )
