/*
能发送异步ajax请求的函数模块
函数返回promise对象
 */
import axios from 'axios'
import {message} from 'antd'

export default function ajax(url, data={}, type='GET'){
    return new Promise
    if(type === 'GET'){ //发送GET请求
        return axios.get(url, {
            params:data
        })
    }else{ //发送POST请求
        return axios.post(url,data)
    }
}
