import React, {Component} from 'react'
import {Card, Button, Table, Modal} from 'antd'

import AddForm from './components/addForm'

/*
管理-统计
 */
export default class UserManage extends Component{
    state = {
        roles: [{
            "name": "admin",
            "menus":"home/statistics/user/authority/base"
        },
        {
            "name": "admin2",
            "menus":"home/statistics/user/authority/base"
        },], //所有角色列表
        role: {}, //选中的role
        showStatus:0, //显示状态，0不显示modal,1显示添加modal，2显示设置modal
    }

    columns = [
        {
            title:'角色名称',
            dataIndex:'name'
        },
    ]

    onRow = (role) => {
        return{
            onClick: event =>{
                this.setState({role})
            }
        }
    }

    addAuthority = ()=>{

    }

    handleCancel = ()=>{
        this.setState({showStatus:0})
    }

    render(){
        const {roles, role, showStatus} = this.state
        const title=(
            <span>
                <Button type='primary' onClick={()=>{this.setState({showStatus:1})}}>
                    创建角色
                </Button> &nbsp;
                <Button type='primary' onClick={()=>{this.setState({showStatus:2})}} disabled={!role.name}>
                    设置角色权限
                </Button>
            </span>
        )
        return(
            <Card title={title}>
                <Table bordered
                    rowKey='name'
                    dataSource={roles}
                    columns={this.columns}
                    rowSelection={{type: 'radio', selectedRowKeys: [role.name]}}
                    onRow={this.onRow}
                />
                <Modal title='添加角色' visible={showStatus===1} 
                    onOk={this.addAuthority} onCancel={this.handleCancel}
                >
                    <AddForm
                        setForm={(form) => this.form = form}
                    />
                </Modal>
                <Modal title="Basic Modal">
                    <p>Some contents...</p>
                    <p>Some contents...</p>
                    <p>Some contents...</p>
                </Modal>
            </Card>
        )
    }
}
