import React, {Component} from 'react'
import {useDrag} from 'react-dnd'


import {Layout, Menu, Button } from 'antd'
import {
    PlusCircleOutlined,
    DeleteOutlined,
  } from '@ant-design/icons';

import FavInput from '../favInput/favInput'
import FavMenu from '../favMenu/favMenu'
import './leftNav.less'

/*
左侧导航的组件
 */
const { Sider } = Layout;

export default class LeftNav extends Component{
    constructor(props){
        super(props)
        const favouritesMenuList = [{rank:0, title:'常用'},{rank:1, title:'文献'}]
        this.state={
            theme:'light',
            menuList:favouritesMenuList,
            onAddStatus: false,
            selectedMenu:favouritesMenuList[0]
        }
    }

    updateMenuList = ()=>{

    }

    getMenuNodes = (menuList)=>{
        return menuList.map(item => {
            const MyMenu = ()=>{
                const [, drager] = useDrag({
                    item: {type: 'menu'}
                })
                return(
                    <Menu.Item ref={drager} key={item.rank}>
                        <FavMenu title={item.title} editFavType={this.editFavType}/>
                    </Menu.Item>
                )
            }
            return(<MyMenu/>)
        })
    }

    onAdd = ()=>{
        this.setState({onAddStatus:true})
    }

    onDel = ()=>{
        console.log('Del' + this.state.selectedMenu)
        //TODO 删除选中项
    }

    onSelect = (value)=>{
        this.setState({selectedMenu:value.key})
    }

    editFavType = (oldName, newName)=>{
        console.log(oldName, newName)
        //TODO 编辑收藏类名
    }

    addFavType = (typeName)=>{
        //TODO 添加收藏类名
        let {menuList} = this.state
        if(typeName){
            const rank = menuList.length
            menuList.push({rank:rank, title:typeName})
        }
        this.setState({onAddStatus:false, menuList:menuList})
    }

    render(){
        const {menuList, onAddStatus} = this.state
        const menuNodes = this.getMenuNodes(menuList)
        return(
            <Sider className="favourites-sider" theme={this.state.theme}>
                <Menu className="favourites-sider-menu" mode="inline"
                defaultSelectedKeys={['0']}
                onSelect={this.onSelect}>
                    {menuNodes}
                    <Menu.Item key='new-menu' style={{display:onAddStatus ? 'block' : 'none'}}>
                        <FavInput editFavType={this.addFavType}/>
                    </Menu.Item>
                </Menu>
                <div>
                    <Button type='link' style={{width:"50%"}} onClick={this.onAdd}>
                        <PlusCircleOutlined style={{ fontSize: '20px'}}/>
                    </Button>
                    <Button type='link' style={{width:"50%"}} onClick={this.onDel}>
                        <DeleteOutlined style={{ fontSize: '20px'}}/>
                    </Button>
                </div>
            </Sider>
        )
    }
}
