import React, {Component} from 'react'
import { Row,Col,Button,Tooltip,Modal } from 'antd';
import { PlusOutlined } from '@ant-design/icons'

import FavouritesCard from '../favCard/favCard'

/*
左侧导航的组件
 */
export default class CardContent extends Component{
    state = {
        showState:0,
    }

    update = ()=> this.props.update()

    getCardNodes = (itemList)=>{
        return itemList.map(item => {
            return(
                <Col key={item.url}>
                    <FavouritesCard url={item.url} title={item.title}
                    description={item.description} rank={item.rank}
                    update={this.update}/>
                </Col>
            )
        })
    }

    onAddItem(){
        console.log(123)
    }

    render(){
        const {itemList} = this.props
        const cardNodes = this.getCardNodes(itemList)
        return(
            <div>
                <Row gutter={[16, 24]}>
                    {cardNodes}
                    <Tooltip title="添加一个">
                        <Button icon={<PlusOutlined style={{fontSize:'50px', color:'#C0C0C0'}}/>} 
                        style={{height:'200px', width:'200px'}} onClick={this.onAddItem}/>
                    </Tooltip>
                </Row>
                <Modal title='添加用户' visible={this.props.showStatus===1} 
                onOk={this.addUser} onCancel={()=>{this.props.addUser()}}
                >
                    <Form ref={this.formRef}>
                        <Form.Item name="username"
                        rules={[
                            {required: true,message: '请输入用户名'},
                            {min: 4,message: '用户名最少4位'},
                            {max: 12,message: '用户名最多12位'},
                            {pattern: /^[a-zA-z0-9_*!]+$/,message: '用户名必须由英文、数字或英文字符_*!组成'},
                        ]}
                        >
                            <Input placeholder="用户名" />
                        </Form.Item>
                        <Form.Item name="password"
                        rules={[
                            {required: true,message: '请输入密码！'},
                            {min: 6,message: '密码最少6位'},
                            {max: 16,message: '密码最多16位'},
                            {pattern: /^[a-zA-z0-9_*!]+$/,message: '密码必须由英文、数字或英文字符_*!组成'},
                        ]}
                        >
                            <Input type="password" placeholder="密码"/>
                        </Form.Item>
                        <Form.Item name="authority" initialValue='root'>
                            <Select>{this.state.options}</Select>
                        </Form.Item>
                    </Form>
                </Modal>
            </div>
        )
    }
}
